import React, { useState } from "react";
import ReactDOM from "react-dom/client"; // Correct import for React 18
import "./styles.css";
import marked from "marked"; // Import marked for Markdown parsing

const App = () => {
  const [markdownText, setMarkdownText] = useState(""); // Markdown text state
  const [htmlText, setHtmlText] = useState(""); // HTML text state

  // Handle real-time markdown to HTML conversion
  const handleMarkdownChange = (event) => {
    const markdownValue = event.target.value;
    setMarkdownText(markdownValue);
    setHtmlText(marked(markdownValue)); // Convert markdown to HTML
  };

  // Sync HTML input and update Markdown (optional feature to edit HTML directly)
  const handleHtmlChange = (event) => {
    setHtmlText(event.target.value);
    setMarkdownText(marked(event.target.value)); // Update markdown live
  };

  return (
    <div className="app">
      <h1>Live Markdown & HTML Editor</h1>
      <div className="editor-container">
        {/* Left Column - Markdown Editor */}
        <div className="editor-section markdown-section">
          <textarea
            className="editor"
            value={markdownText}
            onChange={handleMarkdownChange}
            placeholder="Start typing markdown here"
          />
        </div>

        {/* Right Column - Live HTML Preview */}
        <div className="preview-section">
          <div
            className="output"
            contentEditable={true}
            onInput={handleHtmlChange}
            dangerouslySetInnerHTML={{ __html: htmlText }}
          />
        </div>
      </div>
    </div>
  );
};

const root = ReactDOM.createRoot(document.getElementById("root"));
root.render(<App />);
